using MongoDB.Bson.Serialization.Attributes;namespace CharacterCreator.Core;[BsonDiscriminator(RootClass = true)][BsonKnownTypes(typeof(Equipment))]public class BaseItem{    public string Name { get; set; }    public EquipmentType Type { get; set; }    public BaseItem(string name)    {        Name = name;        Type = EquipmentType.InventoryItem;    }    public override string ToString()    {        return $"{Name} | {Type}";    }}